floor_number = 0
moves = 0

for char in input:

  if char == '(':
    floor_number += 1
    moves += 1

  elif char == ')':
    floor_number -= 1
    moves += 1

  if floor_number == -1:
    break

print('floor =', floor_number, 'moves =', moves)
